import { Transaction } from "./transaction/transaction";
/**
 * 数据库连接类
 */
declare class Connection {
    /**
     * 真实连接对象
     */
    conn: any;
    /**
     * 线程id
     */
    threadId: number;
    /**
     * 连接状态
     */
    connected: boolean;
    /**
     * 创建者对象id
     */
    fromId: number;
    constructor(conn: any);
    /**
     * 关闭连接
     * @param force     是否强制关闭
     */
    close(force?: boolean): Promise<void>;
    /**
     * 创建事务对象
     */
    createTransaction(): Transaction;
}
export { Connection };
