/**
 * relaen 框架管理器
 */
declare class RelaenManager {
    /**
     * 应用名，必须设置
     */
    static appName: string;
    /**
     * 数据库类型
     * mysql oracle mssql
     */
    static dialect: string;
    /**
     * 开启一级cache
     */
    static cache: boolean;
    /**
     * 是否调试模式
     */
    static debug: boolean;
    /**
     * 初始化
     * @param cfg   配置文件名或配置对象
     */
    static init(cfg: any): void;
    /**
     * @exclude
     * 解析实例配置文件
     * @param path      文件路径
     */
    static parseFile(path: string): any;
}
export { RelaenManager };
